mapper.select:
select * from ${params.fromTable} t


		and id = (select max(id) from ${params.fromTable}  and  id < #{id})


		and id = (select min(id) from ${params.fromTable}  and id > #{id} )


		and t.id = #{id}



	order by ${params.order}


	${params.orderBy}
